var time = {
    minutes: 25,
    seconds: 0
};
var paused = false;
var start = false;
var rest = false;
var blocksDone = 0;


setInterval(function () {
    if (start && !paused) {
        //See if have to rollover minute
        if (time.seconds == 0 && time.minutes > 0) {
            time.minutes--;
            time.seconds = 59;
        } else if (time.seconds > 0) {
            time.seconds--;
        } else if (time.seconds == 0 && time.minutes == 0) {
            //do break    
            if (rest == true) {
                if(blocksDone == 4) {
                    resetBlocks();
                }
                doReset("soft");
                rest = false;
            } else {
                console.log("Should do break");
                makeBlockVisible();
            }
            beep();


        }

    }
    updateScreen();
    //Every second minus 1 from the time.

}, 1000);

function makeBlockVisible() {
    //Will make a block visible 
    if (blocksDone < 4) {
        //Make visible 
        //var blockGet = "block" + blocksDone;
        var makeVisible = document.getElementsByClassName("blk")[blocksDone];
        makeVisible.style.visibility = "visible";
        doBreak("Short");
        rest = true;
        blocksDone++;
        if(blocksDone == 4) {
            rest = true;
            doBreak("Long");
        }
    }

}

function resetBlocks() {
    for (i = 0; i < 4; i++) {
        document.getElementsByClassName("blk")[i].style.visibility = "hidden";


    }
    blocksDone = 0;
}

function startClock() {

    start = true;
    if (paused = true) {
        paused = false;
    }

};

function updateScreen() {
    console.log("Doing Update");
    if (time.seconds == 0) {
        document.getElementById("timer").innerHTML = time.minutes + ":00";
        document.title = time.minutes + ":00";

    } else if (time.seconds < 10) {
        document.getElementById("timer").innerHTML = time.minutes + ":0" + time.seconds;
        document.title = time.minutes + ":0" + time.seconds;
    } else {
        document.getElementById("timer").innerHTML = time.minutes + ":" + time.seconds;
        document.title = time.minutes + ":" + time.seconds;

    }

}

function doPause() {
    if (paused == true) {
        paused = false;
    } else {
        paused = true;
    }
}

function doReset(hsoft) {
    document.getElementById("timer").style.backgroundColor = "white";
    if(hsoft == "soft") {
        time.minutes = 25; 
        time.seconds = 0;
    } else {
        
    
    
    rest = false;
    time.minutes = 25;
    time.seconds = 0;
    resetBlocks();
    updateScreen();
    }
}

function doBreak(slong) {
    document.getElementById("timer").style.backgroundColor = "yellow";
    if (slong == "Short") {
        time.minutes = 5;
        time.seconds = 0;

    } else if (slong == "Long") {
        time.minutes = 15;
        time.seconds = 0;

    }

}

function beep() {
    //Stolen from stack overflow soz

    var snd = new Audio("data:audio/wav;base64,");
    snd.play();

}
